import { useContext } from "react";

import { ProductContext } from "../../../context/products.context";
import ProductCard from "../../product-card/product-card.component";

const Shop = () => {
  const { products } = useContext(ProductContext);

  return (
    <div>
      {products.map(({ id, name }) => (
        <ProductCard key={id} />
      ))}
    </div>
  );
};

export default Shop;
