import { useState } from "react";

import {
  createAuthUserWithEmailAndPassword,
  createUserDocumentFromAuth,
} from "../../utils/firebase/firebase.utils";

const defaultFormFields = {
  displayName: "",
  email: "",
  password: "",
  confirmPassword: "",
};

const SignUpForm = () => {
  const [formFields, setFormFields] = useState(defaultFormFields);
  const { displayName, email, password, confirmPassword } = formFields;

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Confirm passwords matches
    if (password !== confirmPassword) return alert("Passwords dont match");

    try {
      // See if we've authenticated that user with the email and password
      const response = await createAuthUserWithEmailAndPassword(
        email,
        confirmPassword,
        displayName
      );
      console.log(response);
    } catch (error) {
      console.error("Something went wrong creating the user", error.message);
    }

    // Create a user document from return
    // return await createUserDocumentFromAuth(response);
  };

  const handleChange = (event) => {
    const { name, value } = event.target;

    setFormFields({ ...formFields, [name]: value });
  };

  return (
    <div>
      <h1>Sign up with your e-mail and password</h1>
      <form onSubmit={handleSubmit}>
        <label for="displayName">Display Name</label>
        <input
          type="text"
          onChange={handleChange}
          name="displayName"
          value={displayName}
          required
        />

        <label for="email">Email</label>
        <input
          type="email"
          onChange={handleChange}
          name="email"
          value={email}
          required
        />

        <label for="password">Password</label>
        <input
          type="password"
          onChange={handleChange}
          name="password"
          value={password}
          required
        />

        <label for="confirmPassword">Confirm password</label>
        <input
          type="password"
          onChange={handleChange}
          name="confirmPassword"
          value={confirmPassword}
          required
        />

        <button type="submit">Sign up</button>
      </form>
    </div>
  );
};

export default SignUpForm;
